INSERT INTO international VALUES (43,'Product',6,'Specifikationer är den tekniska beskrivningen av produkten.\r\n\r\n<b>Etikett</b>\r\nTyp av specifikation. Till exempel höjd, bredd, vikt eller färg. Du kan välja en du valt till andra produkter eller skriva in en nya.\r\n\r\n<b>Specifikation</b>\r\nDet faktiska värdet på etiketten. Valde du till exempel vikt som etikett så sätter du specifikationen till 18 (om din produkt väger 18 kilo).\r\n\r\n<b>Enheter</b>\r\nI vilken enhet mäts etiketten? Valde du vikt ovan som exempel så skriver du \"kg\" eller \"kilo\" här.\r\n\r\n<b>Lägg till fler specifikationer?</b>\r\nVälj Ja om du vill lägga till fler.',1031750622);
