INSERT INTO international VALUES (39,'Product',6,'WebGUI har inbyggt produkthanteringssystem som gör det enkelt för dig att publicera dina produkter och tjänster på webben.\r\n\r\n<b>Pris</b>\r\nProduktens pris. Du kan även skriva in text som till exempel \"ring för offert\", eller lämna fältet tomt.\r\n\r\n<b>Produktnummer</b>\r\nProduktens nummer, SKU, ISBN, eller annan identifiering av denna produkt.\r\n\r\n<b>Produkt bild 1</b>\r\nEn bild på produkten.\r\n\r\n<b>Produkt bild 2</b>\r\nEn bild på produkten.\r\n\r\n<b>Produkt bild 3</b>\r\nEn bild på produkten.\r\n\r\n<b>Broschyr</b>\r\nProduktens broschyr.\r\n\r\n<b>Manual</b>\r\nProdukt-, användar- eller servicemanual för produkten.\r\n\r\n<b>Garanti</b>\r\nProduktens garantivillkor.',1031749615);
