INSERT INTO international VALUES (632,'WebGUI',21,'<b>Id ÍÏÄÕÌÑ</b><br>\r\nId ÍÏÄÕÌÑ ÐÒÅÄÓÔÁ×ÌÑÅÔ ÓÏÂÏÊ ÕÎÉËÁÌØÎÙÊ ÉÄÅÎÔÉÆÉËÁÃÉÏÎÎÙÊ ÎÏÍÅÒ, ÐÒÉÓ×ÁÅÍÙÊ ÍÏÄÕÌÀ ÓÉÓÔÅÍÏÊ Á×ÔÏÍÁÔÉÞÅÓËÉ. éÓÐÏÌØÚÕÅÔÓÑ ÐÒÉ ÎÁÐÉÓÁÎÉÉ SQL- ÚÁÐÒÏÓÏ×.\r\n<br><br>\r\n<b>îÁÚ×ÁÎÉÅ</b><br>\r\nîÁÚ×ÁÎÉÅ ÍÏÄÕÌÑ; ÏÎÏ ÏÂÙÞÎÏ ÏÔÏÂÒÁÖÁÅÔÓÑ ÐÅÒÅÄ ÍÏÄÕÌÅÍ.\r\n<br><br>\r\n<b>ïÔÏÂÒÁÖÁÔØ ÎÁÚ×ÁÎÉÅ</b><br>\r\näÌÑ ÌÀÂÏÇÏ ÍÏÄÕÌÑ ÍÏÖÎÏ ÒÁÚÒÅÛÉÔØ ÉÌÉ ÚÁÐÒÅÔÉÔØ ÏÔÏÂÒÁÖÅÎÉÅ ÅÇÏ ÎÁÚ×ÁÎÉÅ.\r\n<br><br>\r\n<b>÷ÙÐÏÌÎÑÔØ ÍÁËÒÏÓÙ?</b><br>\r\näÌÑ ÌÀÂÏÇÏ ÍÏÄÕÌÑ ÍÏÖÎÏ ÒÁÚÒÅÛÉÔØ ÉÌÉ ÚÁÐÒÅÔÉÔØ ×ÙÐÏÌÎÅÎÉÅ ÍÁËÒÏÓÏ×.\r\n<br><br>\r\n<b>ðÏÚÉÃÉÑ ×ÎÕÔÒÉ ÛÁÂÌÏÎÁ</b><br>\r\níÏÖÎÏ ×ÙÂÒÁÔØ ÐÏÚÉÃÉÀ ÍÏÄÕÌÑ ×ÎÕÔÒÉ ÛÁÂÌÏÎÁ ÓÔÒÁÎÉÃÙ.\r\n<br><br>\r\n<b>äÁÔÁ ÎÁÞÁÌÁ</b><br>\r\n÷ ÜÔÏÍ ÐÏÌÅ ÎÁÄÏ ÕËÁÚÁÔØ ÄÁÔÕ, ÎÁÞÉÎÁÑ Ó ËÏÔÏÒÏÊ ÍÏÄÕÌØ ÓÔÁÎÏ×ÉÔÓÑ ×ÉÄÉÍÙÍ ÄÌÑ ÐÏÓÅÔÉÔÅÌÅÊ ÓÁÊÔÁ.\r\n<br><br>\r\n<b>äÁÔÁ ÏËÏÎÞÁÎÉÑ</b><br>\r\n÷ ÜÔÏÍ ÐÏÌÅ ÕËÁÚÙ×ÁÅÔÓÑ ÄÁÔÁ, ÐÏÓÌÅ ËÏÔÏÒÏÊ ÍÏÄÕÌØ ÓÔÁÎÏ×ÉÔÓÑ ÎÅ×ÉÄÉÍÙÍ ÄÌÑ ÐÏÓÅÔÉÔÅÌÅÊ ÓÁÊÔÁ.\r\n<br><br>\r\n<b>ïÐÉÓÁÎÉÅ</b><br> \r\n÷ ÜÔÏÍ ÐÏÌÅ ÏÂÙÞÎÏ ÕËÁÚÙ×ÁÀÔÓÑ ÏÓÎÏ×ÎÙÅ ÆÕÎËÃÉÉ ÍÏÄÕÌÑ. ðÏÌÅ ÍÏÖÎÏ ÏÓÔÁ×ÉÔØ ÐÕÓÔÙÍ.\r\n<br><br>',1048516549);
