INSERT INTO international VALUES (651,'WebGUI',3,'Bij het legen van de prullenbak wordt alles wat erin zit voor altijd weggegooid. Voor zaken waar u niet zeker van bent of ze wel weggegooid moeten kunt u het beste even in het klemboord knippen voordat u de prullenbak leegt.',1038483463);
INSERT INTO international VALUES (633,'WebGUI',3,'Simpel gezegd zijn \'roots\' pagina\'s zonder hogerliggende pagina. De eerste en belangrijkste \'root\' in WebGUI is de \"Home\" pagina. In de meeste gevallen is een extra \'root\' nooit nodig. Voorbeelden voor een extra \'root\' zijn een verborgen pagina voor administratieve redenen of een nieuwe \'root\' om de zoekmachine op te plaatsen.',1038483226);
INSERT INTO international VALUES (630,'WebGUI',3,'WebGUI heeft een kleine, maar degelijke real-time zoekmachine ingebouwd.\r\nWanneer u de zoemachine wilt gebruiken kunt u het ^?; macro toepassen, u\r\nkunt ook ?op=search aan het eind van een URL toevoegen of een apart formulier\r\nbouwen om het aan te spreken.<br>\r\n<br>\r\nVeel mensen hebben een specifieke zoekmachine nodig om hun WebGUI site en\r\nenkele anderen te indexeren. In dat geval raden we <a\r\n href=\"http://www.mnogosearch.org/\">MnoGo Search</a> of <a\r\n href=\"http://www.htdig.org/\">ht://Dig</a> aan. <br>\r\n',1039782428);
INSERT INTO international VALUES (629,'WebGUI',3,'<b>Voorkom Proxy Caching</b><br>\r\n Sommige bedrijven hebben proxyservers die problemen met WebGUI kunnen opleveren. \r\nWanneer er problemen met WebGUI geconstateerd worden en er wordt tevens gebruik \r\ngemaakt van een proxyserver is het wellicht verstandig deze instelling op \r\nJa te zetten.<br>\r\n <i>Let op: </i>WebGUI\'s URLs zullen er niet zo gebruikersvriendelijk uit \r\nzien wanneer deze optie aan staat<br>\r\n <br>\r\n <b>Laat debugging zien</b><br>\r\n Wanneer deze optie aan staat worden alle variabelen die gebruikt worden\r\ndoor WebGUI onderaan uw pagina getoond.<br>\r\n <i>Let op:</i> Wanneer debugging aan staat zijn veel security gevoelige\r\ngegevens voor iedereen te zien zoals het wachtwoord van uw SQL database.\r\nGebruik deze setting dan ook alleen op ontwikkelservers.<br>\r\n <b><br>\r\n Houdt pagina statistieken bij?</b><br>\r\n Deze optie zet het verzamelen van pagina statistieken aan. Het genereert \r\nwat extra belasting op uw database en processor maar geeft uw inzicht in hoe\r\nvaak pagina\'s gebruikt worden.<br>\r\n <br>\r\n',1039782402);
INSERT INTO international VALUES (627,'WebGUI',3,'Profielen worden gebruikt om informatie over een bepaalde gebruiker uit te breiden. In sommige gevallen zijn profielen belangrijk voor een site, in andere gevallen niet. Het profielensysteem is volledig uitbreidbaar.',1038480954);
INSERT INTO international VALUES (625,'WebGUI',3,'<b>Naam<br>\r\n </b>Het label waaraan naar dit plaatje gerefereerd kan worden wanneer het \r\nin pagina\'s geplaatst wordt met ^I(\"label\");<br>\r\n <br>\r\n <b>Bestand</b><br>\r\n Kies een bestand van je lokale harddisk om op de server te zetten.<br>\r\n <br>\r\n <b>Parameters</b><br>\r\n Hier kunnen HTML &lt;img&gt; parameters toegevoegd worden die voor dit plaatje \r\ngebruikt zullen worden.<br>\r\n <br>\r\n <i>Voorbeelden:</i><br>\r\n align=\"right\"<br>\r\n alt=\"Beschrijving voor dit plaatje\"<br>\r\n width=\"300\" (breedte in pixels)<br>\r\n height=\"200\" (hoogte in pixels)<br>\r\n <b><br>\r\nPictogram grootte</b><br>\r\nHoe groot (in pixels) moet het pictogram voor dit plaatje zijn?<br>\r\n',1039782372);
