INSERT INTO international VALUES (77,'FAQ',8,'Questa è la lista delle variabili disponibili nel FAQ templates.\r\n<p><b>addquestion.url</b><br>\r\nL\'URL per aggiungere una domanda alle FAQ\r\n<p><b>addquestion.label</b><br>\r\nL\'etichetta per il link per aggiungere la domanda.\r\n<p><b>qa_loop</b><br>\r\nIl ciclo contenente le variabili per ogni domanda nelle FAQ. \r\n<blockquote>\r\n  <b>qa.id</b><br>\r\n  L\'identificatore univoco per questa domanda.\r\n  <p><b>qa.answer</b><br>\r\n  La risposta per questa domanda.\r\n  <p><b>qa.question</b><br>\r\n  La domanda stessa.\r\n  <p><b>qa.controls</b><br>\r\n  I controlli di gestione di WebGUI per questa domanda.\r\n  <p>&nbsp;</p>\r\n</blockquote>\r\n',1045680066);
