INSERT INTO international VALUES (73,'Article',6,'Följande variabler är tillgängliga i artikelmallar.\r\n\r\n<p/><b>attachment.box</b><br/>\r\nGet en standard WebGUI bilagetruta med ikon, filnamn och bilageindikator.\r\n\r\n<p/><b>attachment.icon</b><br/>\r\nURL:en till ikonbilden för denna bilagstyp.\r\n\r\n<p/><b>attachment.name</b><br/>\r\nFilnamnet för denna bilaga.\r\n\r\n<p/><b>attachment.url</b><br/>\r\nURL:en för att ladda ner denna bilaga.\r\n\r\n<p/><b>image.thumbnail</b><br/>\r\nURL:en till tumnagelsbilden för den bilagda bilden.\r\n\r\n<p/><b>image.url</b><br/>\r\nURL:en för den bilagda bilden.\r\n\r\n<p/><b>post.label</b><br/>\r\nEn översatt etikett för att addera en kommentar till denna artikel.\r\n\r\n<p/><b>post.URL</b><br/>\r\nURL:en för att addera en kommentar till denna artikel.\r\n\r\n<p/><b>replies.count</b><br/>\r\nAntal svar på denna artikel. \r\n\r\n<p/><b>replies.label</b><br/>\r\nEn översatt!\n etikett som indikerar att man kan titta på svar.\r\n\r\n<p/><b>replies.url</b><br/>\r\nURL:en för att titta på svar till denna artikel. \r\n',1047899888);
INSERT INTO international VALUES (75,'USS',6,'Detta är en lista av defaultvariabler som är tillgängliga i mallarna till användarnas inläggssystem.\r\n<p/>\r\n<b>readmore.label</b>\r\n<br>\r\nEn översatt etikett som indikerar att användaren skall klicka för att läsa mer.\r\n<p/>\r\n<b>responses.label</b>\r\n<br>\r\nEn översatt etikett som indikerar att användaren skall klicka för att se svaren för detta inlägg.\r\n<p/>\r\n<b>canPost</b>\r\n<br>\r\nEtt vilkor som indikerar huruvida en användare kan lägga till ett nytt inlägg.\r\n<p/>\r\n<b>post.url</b>\r\n<br>\r\nURL för att addera inlägg.\r\n<p/>\r\n<b>post.label</b>\r\n<br>\r\nEn översatt etikett för länken att addera inlägg.\r\n<p/>\r\n<b>search.label</b>\r\n<br>\r\nEn översatt etikett för söklänken.\r\n<p/>\r\n<b>search.url</b>\r\n<br>\r\nURL för att slå på/av WebGUI\'s sökformulär.\r\n<p/>\r\n<b>search.form</b>\r\n<br>\r\nWebGUI\'s sökformulär.\r\n<p/>\r\n<b>title!\n.label</b>\r\n<br>\r\nEn översatt etikett för titelkolumnen.\r\n<p/>\r\n<b>thumbnail.label</b>\r\n<br>\r\nEn översatt etikett för tumnagelskolumnen.\r\n<p/>\r\n<b>date.label</b>\r\n<br>\r\nEn översatt etikett för datumkolumnen.\r\n<p/>\r\n<b>by.label</b>\r\n<br>\r\nEn översatt etikett som informerar om vem som skickat inlägget.\r\n<p/>\r\n<b>submissions_loop</b>\r\n<br>\r\nEn loop innehållande varje inlägg.\r\n<p/>\r\n<blockquote>\r\n<b>submission.id</b><br/>\r\nUnik identifierare för detta inlägg. \r\n\r\n<p/><b>submission.url</b><br/>\r\nURL för att visa detta inlägg. \r\n\r\n<p/><b>submission.content</b><br/>\r\nFörkortat innehåll av detta inlägg.\r\n\r\n<p/><b>submission.responses</b><br/>\r\nAntal svar för detta inlägg. \r\n\r\n<p/><b>submission.title</b><br/>\r\nTiteln för detta inlägg. \r\n\r\n<p/><b>submission.userId</b><br/>\r\nUser-id för den användare som postade inlägget. \r\n\r\n<p/><b>submission.username</b><br/>\r\nAnvändarnamnet för den person som postade inl!\nägget. \r\n\r\n<p/><b>submission.status</b><br/>\r\nStatus för detta inlägg (approved, pending, denied). \r\n\r\n<p/><b>submission.thumbnail</b><br/>\r\nTumnagelbild som skickades med detta inlägg (om det finns någon). \r\n\r\n<p/><b>submission.date</b><br/>\r\nDatum när detta inlägg skickades. \r\n\r\n<p/><b>submission.currentUser</b><br/>\r\nTillstånd som indikerar som inloggad användare var den som skickade inlägget. \r\n\r\n<p/><b>submission.userProfile</b><br/>\r\nURL till profilen för den användare som skickade inlägget.\r\n\r\n<p/><b>submission.secondColumn</b><br/>\r\nVilkor som indikerar om detta inlägg hör till den andra kolumnen i en flerkolumns-layout.\r\n\r\n<p/><b>submission.thirdColumn</b><br/>\r\nVilkor som indikerar om detta inlägg hör till den tredje kolumnen i en flerkolumns-layout.\r\n\r\n\r\n<p/><b>submission.fourthColumn</b><br/>\r\nVilkor som indikerar om detta inlägg hör till den fjärde kolumnen i en flerkolumns-layout.\r\n\r\n<p/><b>submission.fifthC!\nolumn</b><br/>\r\nVilkor som indikerar om detta inlägg hör till den femte kolumnen i en flerkolumns-layout.\r\n\r\n</blockquote>\r\n<p/><b>firstPage</b><br/>\r\nLänk till första sidan i sidlistan.\r\n\r\n<p/><b>lastPage</b><br/>\r\nLänk till sisda sidan i sidlistan.\r\n\r\n<p/><b>nextPage</b><br/>\r\nLänk till nästa sida i listan.\r\n\r\n<p/><b>previousPage</b><br/>\r\nLänk till föregående sida i listan.\r\n\r\n<p/><b>pageList</b><br/>\r\nEn lista av länkar till alla sidor.\r\n\r\n<p/><b>multiplePages</b><br/>\r\nVilkor som indikerar om det finns fler än en sida i sidlistan.\r\n',1047899555);
