INSERT INTO international VALUES (73,'SyndicatedContent',2,'Nachfolgend die Template Variablen für das Syndicated Content Template:\r\n\r\n<b>channel.title</b>\r\nDer Titel dieses Contentbereichs\r\n<b>channel.description</b>\r\nEine Beschreibung dieses Channels\r\n<b>channel.link</b>\r\nEine URL, die auf die Original-Seite dieses Channels verlinkt.\r\n<b>item_loop</b>\r\nEine Schleife, die die Daten dieses Channels enthält.\r\n<b>title</b>\r\nDer Titel des Channelbeitrags\r\n<b>description</b>\r\nDie Beschreibung dieses Beitrags\r\n<b>link</b>\r\nEine URL zum Beitrag',1048242285);
