INSERT INTO international VALUES (73,'SyndicatedContent',12,'<DIV align=left>The following are the template variables available to the \r\nSyndicated Content template. \r\n<P><B>channel.title</B><BR>The title of this piece of syndicated content. \r\n<P><B>channel.description</B><BR>A description of the content available through \r\nthis channel. \r\n<P><B>channel.link</B><BR>A URL back to the originating site of this channel. \r\n<P><B>item_loop</B><BR>A loop containing the data from this channel. \r\n<BLOCKQUOTE><B>title</B><BR>The title of a piece of content. \r\n<P><B>description</B><BR>The description of the content. \r\n<P><B>link</B> A URL directly to the original content.</P></BLOCKQUOTE></DIV>',1048546157);
