INSERT INTO international VALUES (47,'Product',6,'Relaterade produkter är produkter som är likvärdiga eller som kompletterar varandra.\r\n\r\n<b>Relaterade produkter</b>\r\nVälj en produkt från listan med tidigare skapade produkter.\r\n\r\n<b>Fortsätt lägga till relaterad produkt?</b>\r\nVälj Ja om du har ytterligare relaterade produkter.',1031751664);
