INSERT INTO international VALUES (45,'Product',6,'Tillbehör är produkter som kan utöka och/eller förbättra andra produkter.\r\n\r\n<b>Tillbehör</b>\r\nVälj från en lista med redan upplagda produkter.\r\n\r\n<b>Fortsätt med att lägga till tillbehör?</b>\r\nVälj Ja om du vill fortsätta lägga till tillbehör.',1031750754);
