INSERT INTO international VALUES (41,'Product',6,'Egenskaper är säljande fraser för produkten. Används som försäljningsargument för ökad försäljning.\r\n\r\n<b>Egenskap</b>\r\nDu kan lägga till en ny egenskap eller välja en som du skrivit på en tidigare inlagd produkt.\r\n\r\n<b>Fortsätt med att lägga till egenskaper?</b>\r\nSvara Ja om du vill fortsätta lägga till egenskaper.',1031749794);
