INSERT INTO international VALUES (71,'SyndicatedContent',6,'Syndikatinnehll r innehll som tas frn extern webbplats. Formatet p innehllet r RDF/RSS. Denna teknik anvnds ofta fr att dra nyhetsrubriker frn webplatser som <a href=\"http://www.cnn.com/\">CNN</a> och <a href=\"http://slashdot.org/\">Slashdot</a>. Det kan sjlvklart anvndas fr andra saker som sportresultat och aktiekurser.\r\n\r\n<b>URL till RSS-fil</b>\r\nSkriv in exakt URL till (inled med http://) till innehllets RDF eller RSS-fil. Innehller kommer laddas ner varje timme.\r\n\r\nDu kan hitta innehll p fljande webbplatser:\r\n\r\n<ul>\r\n<li><a href=\"http://www.newsisfree.com\">http://www.newsisfree.com</a></li>\r\n<li><a href=\"http://www.syndic8.com\">http://www.syndic8.com</a></li>\r\n<li><a href=\"http://www.voidstar.com/node.php?id=144\">http://www.voidstar.com/node.php?id=144</a></li>\r\n<li><a href=\"http://my.userland.com\">http://my.userland.com</a></li>\r\n<li><a href=\"http://www.webreference.com/services/news/\">http://www.webreference.com/services/news/</a></li>\r\n<li><a href=\"http://www.xmltree.com\">http://www.xmltree.com</a></li>\r\n<li><a href=\"http://w.moreover.com/\">http://w.moreover.com/</a></li>\r\n</ul>',1031834039,NULL);
