INSERT INTO international VALUES (43,'Product',6,'Specifikationer r den tekniska beskrivningen av produkten.\r\n\r\n<b>Etikett</b>\r\nTyp av specifikation. Till exempel hjd, bredd, vikt eller frg. Du kan vlja en du valt till andra produkter eller skriva in en nya.\r\n\r\n<b>Specifikation</b>\r\nDet faktiska vrdet p etiketten. Valde du till exempel vikt som etikett s stter du specifikationen till 18 (om din produkt vger 18 kilo).\r\n\r\n<b>Enheter</b>\r\nI vilken enhet mts etiketten? Valde du vikt ovan som exempel s skriver du \"kg\" eller \"kilo\" hr.\r\n\r\n<b>Lgg till fler specifikationer?</b>\r\nVlj Ja om du vill lgga till fler.',1031750622,NULL);
