INSERT INTO international VALUES (39,'Product',6,'WebGUI har inbyggt produkthanteringssystem som gr det enkelt fr dig att publicera dina produkter och tjnster p webben.\r\n\r\n<b>Pris</b>\r\nProduktens pris. Du kan ven skriva in text som till exempel \"ring fr offert\", eller lmna fltet tomt.\r\n\r\n<b>Produktnummer</b>\r\nProduktens nummer, SKU, ISBN, eller annan identifiering av denna produkt.\r\n\r\n<b>Produkt bild 1</b>\r\nEn bild p produkten.\r\n\r\n<b>Produkt bild 2</b>\r\nEn bild p produkten.\r\n\r\n<b>Produkt bild 3</b>\r\nEn bild p produkten.\r\n\r\n<b>Broschyr</b>\r\nProduktens broschyr.\r\n\r\n<b>Manual</b>\r\nProdukt-, anvndar- eller servicemanual fr produkten.\r\n\r\n<b>Garanti</b>\r\nProduktens garantivillkor.',1031749615,NULL);
