INSERT INTO international VALUES (73,'EventsCalendar',6,'<b>Titel</b>\r\nTiteln fr denna hndelse.\r\n\r\n<b>Beskrivning</b>\r\nBeskrivning av aktiviteter fr denna hndelse eller vrig information om aktiviteten.\r\n\r\n<b>Start datum</b>\r\nVilket datum kommer det brja?\r\n\r\n<b>Slut datum</b>\r\nVilket datum slutar det p?\r\n\r\n<b>terkommer varje</b>\r\nVlj ett terkomstintervall fr denna hndelse.\r\n\r\n<b>Fortstt med att lgga till hndelse?</b>\r\nOm du vill fortstta lgga till hndelser lter du \"Ja\" vara valt.',1031667712,NULL);
INSERT INTO international VALUES (71,'EventsCalendar',6,'Hndelsekalendrar anvnds p mnga intrant fr att hlla reda p interna datum som pverkar hela organisationen. Kalendrar p handelsinriktade webbplatser r ett bra stt att lta kunderna veta bl.a. vilka mssor/utstllningar du kommer att medverka p.\r\n\r\n<b>Visningsformat</b>\r\nDetta kan sttas till <i>Lista</i> eller <i>kalender</i>. Nr du vljer <i>lista</i> kommer hndelserna vara listade efter datum (hndelser som redan intrffat kommer inte att visas). Den hr typen passar bst nr det inte hnder mer n ett par saker varje mnad. Om du vljer <i>kalender</i> s kommer hndelserna visas p en mnadskalender som det gr att blddra i. Den hr formatet passar sig bst vid mnga hndelser varje mnad.\r\n\r\n<b>Radbryt efter</b>\r\nNr du anvnder <i>list</i>-formatet s anger du hr hur mnga hndelser som kommer visas per sida.\r\n\r\n<b>Fortstt med att lgga till hndelse?</b>\r\nLt den hr vara instlld p \"Ja\" fr att direkt kunna lgga in hndelser.\r\n\r\n<hr>\r\n<b><i>Note:</i></b> Fljande stil r specifik fr hndelsekalendern.\r\n\r\n<b>.eventTitle</b>\r\nTiteln fr en enskild hndelse.',1031667571,NULL);
