INSERT INTO international VALUES (47,'Product',3,'Gerelateerde produkten zijn produkten die vergelijkbaar zijn met een produkt of die een aanvulling op een produkt.<br>\r\n<br>\r\n<b>Gerelateerde produkten</b><br>\r\nTe kiezen van de lijst met produkten die al ingevoerd zijn.<br>\r\n<br>\r\n<b>Nog een gerelateerd produkt toevoegen?</b><br>\r\nSelecteer \"Ja\" als er nog een gerelateerd produkt toegevoegd gaat worden.<br>',1043314348,NULL);
