INSERT INTO international VALUES (43,'Product',3,'Specificaties zijn de technische details van een produkt.<br>\r\n<br>\r\n<b>Etiket</b><br>\r\nHet type specificatie, bijvoorbeeld hoogte, breedte of kleur. Er kan een\r\nspecificatie gekozen worden die al voor een ander produkt zijn ingevoerd\r\nof er kan een nieuwe specificatie ingevoerd worden.<br>\r\n<br>\r\n<b>Specificatie</b><br>\r\nDe waarde van de specficatie. Als bijvoorbeeld de hoogte als etiket is gekozen,\r\nkan er een geatalswaarde als \"18\" ingevoerd worden.<br>\r\n<br>\r\n<b>Units</b><br>\r\nde eenheid waarin een specificatie wordt uitgedrukt. Als hoogte het label\r\nis kan de eenheid \"meter\" zijn.<br>\r\n<br>\r\n<b>Nog een specificatie toevoegen?</b><br>\r\nAls het gewenst is nog een specificatie in te voeren, slecteer dan \"Ja\".<br>\r\n',1037889959,NULL);
