INSERT INTO international VALUES (41,'Product',3,'Kenmerken zijn de redenen om een product te kopen. Kenmerken zijn vaak voordelen,\r\ndus wanneer gewenst kunnen deze kenmerken aan het produkt toegevoegd worden.<br>\r\n<br>\r\n<b>Kenmerken</b><br>\r\nHier kan een nieuw kenmerk ingevoerd worden of een kenmerk van een ander\r\nprodukt uit het systeem geselecteerd worden.<br>\r\n<br>\r\n<b>Voeg nog een kenmerk toe</b><br>\r\nWanneer meteen een volgend kenmerk toegevoegd gaat worden, klik dan op \"Ja\".<br>\r\n',1037889520,NULL);
