INSERT INTO international VALUES (39,'Product',3,'WebGUI heeft een ingebouwd produkt management systeem, zodat produkten en \r\ndiensten snel en makkelijk gepubliceerd kunnen worden.<br>\r\n <br>\r\n <b>Sjabloon<br>\r\n</b>Kies de layout voor dit product.<b><br>\r\n<br>\r\nPrijs</b><br>\r\n De prijs van het produkt. Hier kan optioneel \"Prijs op te vragen per telefoon\" \r\ningevuld worden, of de prijs kan weggelaten worden.<br>\r\n <br>\r\n <b>Produktnummer</b><br>\r\n Het produktnummer, SKU, ISBN of een andere indentificatie van dit produkt.<br>\r\n <br>\r\n <b>Afbeelding van het produkt 1</b><br>\r\n Een afbeelding van het produkt.<br>\r\n <br>\r\n <b>Afbeelding van het produkt 2</b><br>\r\n  Een afbeelding van het produkt.<br>\r\n <br>\r\n <b>Afbeelding van het produkt 3</b><br>\r\n  Een afbeelding van het produkt.<br>\r\n <br>\r\n <b>Brochure</b><br>\r\n De brochure van dit produkt<br>\r\n <br>\r\n <b>Handleiding</b><br>\r\n De produkt, gebruiks of onderhouds handleiding van dit produkt.<br>\r\n <br>\r\n <b>Garantie</b><br>\r\n De Garantie van dit produkt.<br>\r\n <br>\r\n',1039776947,NULL);
