INSERT INTO international VALUES (95,'EventsCalendar',8,'<p>Le seguenti variabili dei template sono disponibili per personalizzare il\nCalendario Eventi.</p>\n<p><b>addevent.url</b><br>\nL\'URL da inserire in un evento nel calendario.\n<p><b>addevent.label</b><br>\nL\'etichetta tradotta per il link aggiungi evento.\n<p><b>calendar.big</b><br>\nUn Calendario a piena pagina.\n<p><b>calendar.small</b><br>\nUn calendario piccolo che mostra solo le date.\n<p><b>calendar.firstPage<br>\n</b>Un Link alla prima pagina dell\'impaginazione del calendario.\n<p><b>calendar.lastPage</b><br>\nUn Link all\'ultima pagina dell\'impaginazione del calendario.\n<p><b>calendar.nextPage</b><br>\nUn Link alla prossima pagina dell\'impaginazione del calendario.\n<p><b>calendar.previousPage</b><br>\nUn Link alla pagina precedente dell\'impaginazione del calendario.\n<p><b>calendar.pageList</b><br>\nUna lista di link a tutte le pagine dell\'impaginazione del calendario.\n<p><b>calendar.multiplePages</b><br>\nUna variabile che indica dove esiste pi di una pagina nell\'impaginazione del\ncalendario.\n<p><b>list_loop</b><br>\nUn ciclo contenente la visualizzazione a lista di un Calendario Eventi.\n<blockquote>\n  <b>list.date</b><br>\n  La data di questo evento.\n  <p><b>list.title</b><br>\n  Il titolo di questo evento.\n  <p><b>list.description</b><br>\n  La descrizione di questo evento.\n  <p><b>list.sameAsPrevious</b><br>\n  Una variabile che indica se questo evento ha la stessa data dell\'ultimo\n  evento.\n  <p><b>list.url</b><br>\n  L\'URL della pagina piena di questo evento.\n  <p><b>list.controls</b><br>\n  I controlli di WebGUI per la gestione di questo evento.\n  <p>&nbsp;</p>\n</blockquote>\n<p><b>list.firstPage</b><br>\nUn Link alla prima pagina dell\'impaginazione.\n<p><b>list.lastPage</b><br>\nUn Link all\'ultima pagina dell\'impaginazione.\n<p><b>list.nextPage</b><br>\nUn Link alla prossima pagina dell\'impaginazione.\n<p><b>list.previousPage</b><br>\nUn Link alla precedente pagina dell\'impaginazione del calendario.\n<p><b>list.pageList</b><br>\nUna lista di link a tutte le pagine dell\'impaginazione.\n<p><b>list.multiplePages</b><br>\nUna variabile che indica dove esiste pi di una pagina nell\'impaginazione.</p>\n\n',1054313654,NULL);
