INSERT INTO international VALUES (73,'Article',6,'Fljande variabler r tillgngliga i artikelmallar.\r\n\r\n<p/><b>attachment.box</b><br/>\r\nGet en standard WebGUI bilagetruta med ikon, filnamn och bilageindikator.\r\n\r\n<p/><b>attachment.icon</b><br/>\r\nURL:en till ikonbilden fr denna bilagstyp.\r\n\r\n<p/><b>attachment.name</b><br/>\r\nFilnamnet fr denna bilaga.\r\n\r\n<p/><b>attachment.url</b><br/>\r\nURL:en fr att ladda ner denna bilaga.\r\n\r\n<p/><b>image.thumbnail</b><br/>\r\nURL:en till tumnagelsbilden fr den bilagda bilden.\r\n\r\n<p/><b>image.url</b><br/>\r\nURL:en fr den bilagda bilden.\r\n\r\n<p/><b>post.label</b><br/>\r\nEn versatt etikett fr att addera en kommentar till denna artikel.\r\n\r\n<p/><b>post.URL</b><br/>\r\nURL:en fr att addera en kommentar till denna artikel.\r\n\r\n<p/><b>replies.count</b><br/>\r\nAntal svar p denna artikel. \r\n\r\n<p/><b>replies.label</b><br/>\r\nEn versatt!\n etikett som indikerar att man kan titta p svar.\r\n\r\n<p/><b>replies.url</b><br/>\r\nURL:en fr att titta p svar till denna artikel. \r\n',1047899888,NULL);
INSERT INTO international VALUES (75,'USS',6,'Detta r en lista av defaultvariabler som r tillgngliga i mallarna till anvndarnas inlggssystem.\r\n<p/>\r\n<b>readmore.label</b>\r\n<br>\r\nEn versatt etikett som indikerar att anvndaren skall klicka fr att lsa mer.\r\n<p/>\r\n<b>responses.label</b>\r\n<br>\r\nEn versatt etikett som indikerar att anvndaren skall klicka fr att se svaren fr detta inlgg.\r\n<p/>\r\n<b>canPost</b>\r\n<br>\r\nEtt vilkor som indikerar huruvida en anvndare kan lgga till ett nytt inlgg.\r\n<p/>\r\n<b>post.url</b>\r\n<br>\r\nURL fr att addera inlgg.\r\n<p/>\r\n<b>post.label</b>\r\n<br>\r\nEn versatt etikett fr lnken att addera inlgg.\r\n<p/>\r\n<b>search.label</b>\r\n<br>\r\nEn versatt etikett fr sklnken.\r\n<p/>\r\n<b>search.url</b>\r\n<br>\r\nURL fr att sl p/av WebGUI\'s skformulr.\r\n<p/>\r\n<b>search.form</b>\r\n<br>\r\nWebGUI\'s skformulr.\r\n<p/>\r\n<b>title!\n.label</b>\r\n<br>\r\nEn versatt etikett fr titelkolumnen.\r\n<p/>\r\n<b>thumbnail.label</b>\r\n<br>\r\nEn versatt etikett fr tumnagelskolumnen.\r\n<p/>\r\n<b>date.label</b>\r\n<br>\r\nEn versatt etikett fr datumkolumnen.\r\n<p/>\r\n<b>by.label</b>\r\n<br>\r\nEn versatt etikett som informerar om vem som skickat inlgget.\r\n<p/>\r\n<b>submissions_loop</b>\r\n<br>\r\nEn loop innehllande varje inlgg.\r\n<p/>\r\n<blockquote>\r\n<b>submission.id</b><br/>\r\nUnik identifierare fr detta inlgg. \r\n\r\n<p/><b>submission.url</b><br/>\r\nURL fr att visa detta inlgg. \r\n\r\n<p/><b>submission.content</b><br/>\r\nFrkortat innehll av detta inlgg.\r\n\r\n<p/><b>submission.responses</b><br/>\r\nAntal svar fr detta inlgg. \r\n\r\n<p/><b>submission.title</b><br/>\r\nTiteln fr detta inlgg. \r\n\r\n<p/><b>submission.userId</b><br/>\r\nUser-id fr den anvndare som postade inlgget. \r\n\r\n<p/><b>submission.username</b><br/>\r\nAnvndarnamnet fr den person som postade inl!\ngget. \r\n\r\n<p/><b>submission.status</b><br/>\r\nStatus fr detta inlgg (approved, pending, denied). \r\n\r\n<p/><b>submission.thumbnail</b><br/>\r\nTumnagelbild som skickades med detta inlgg (om det finns ngon). \r\n\r\n<p/><b>submission.date</b><br/>\r\nDatum nr detta inlgg skickades. \r\n\r\n<p/><b>submission.currentUser</b><br/>\r\nTillstnd som indikerar som inloggad anvndare var den som skickade inlgget. \r\n\r\n<p/><b>submission.userProfile</b><br/>\r\nURL till profilen fr den anvndare som skickade inlgget.\r\n\r\n<p/><b>submission.secondColumn</b><br/>\r\nVilkor som indikerar om detta inlgg hr till den andra kolumnen i en flerkolumns-layout.\r\n\r\n<p/><b>submission.thirdColumn</b><br/>\r\nVilkor som indikerar om detta inlgg hr till den tredje kolumnen i en flerkolumns-layout.\r\n\r\n\r\n<p/><b>submission.fourthColumn</b><br/>\r\nVilkor som indikerar om detta inlgg hr till den fjrde kolumnen i en flerkolumns-layout.\r\n\r\n<p/><b>submission.fifthC!\nolumn</b><br/>\r\nVilkor som indikerar om detta inlgg hr till den femte kolumnen i en flerkolumns-layout.\r\n\r\n</blockquote>\r\n<p/><b>firstPage</b><br/>\r\nLnk till frsta sidan i sidlistan.\r\n\r\n<p/><b>lastPage</b><br/>\r\nLnk till sisda sidan i sidlistan.\r\n\r\n<p/><b>nextPage</b><br/>\r\nLnk till nsta sida i listan.\r\n\r\n<p/><b>previousPage</b><br/>\r\nLnk till fregende sida i listan.\r\n\r\n<p/><b>pageList</b><br/>\r\nEn lista av lnkar till alla sidor.\r\n\r\n<p/><b>multiplePages</b><br/>\r\nVilkor som indikerar om det finns fler n en sida i sidlistan.\r\n',1047899555,NULL);
