INSERT INTO international VALUES (73,'SyndicatedContent',2,'<p>Nachfolgend die Template Variablen fr das Syndicated Content Template:</p>\r\n<p><b>channel.title</b><br>\r\nDer Titel dieses Contentbereichs</p>\r\n<p><b>channel.description</b><br>\r\nEine Beschreibung dieses Channels</p>\r\n<p><b>channel.link</b><br>\r\nEine URL, die auf die Original-Seite dieses Channels verlinkt.</p>\r\n<p><b>item_loop</b><br>\r\nEine Schleife, welche die Daten dieses Channels enthlt.</p>\r\n<p><b>title</b><br>\r\nDer Titel des Channelbeitrags</p>\r\n<p><b>description</b><br>\r\nDie Beschreibung dieses Beitrags</p>\r\n<p><b>link</b><br>\r\nEine URL zum Beitrag</p>',1067897913,NULL);
