INSERT INTO international VALUES (95,'EventsCalendar',15,'Koristei sljedee varijable u predlocima moete prilagoditi va kalendar dogaaja.\r\n\r\naddevent.url\r\nURL kojim se dogaaj dodaje u kalendar. \r\n\r\naddevent.label\r\nOznaka uz link na dogaaj. \r\n\r\ncalendar.big\r\nKalendar preko cijele stranice. \r\n\r\ncalendar.small\r\nMali kalendar kojim se prikazuju samo datumi. \r\n\r\ncalendar.firstPage\r\nLink na prvu stranu kalendara ukoliko je kalendar rastavljen na vie stranica. \r\n\r\ncalendar.lastPage\r\nLink na posljednju stranu kalendara ukoliko je kalendar rastavljen na vie stranica. \r\n\r\ncalendar.nextPage\r\nLink na sljedeu stranu kalendara ukoliko je kalendar rastavljen na vie stranica. \r\n\r\ncalendar.previousPage\r\nLink na prethodnu stranu kalendara ukoliko je kalendar rastavljen na vie stranica. \r\n\r\ncalendar.pageList\r\nLink na sve stranice kalendara ukoliko je kalendar rastavljen\n  na vie stranica. \r\n\r\ncalendar.multiplePages\r\nUvjetna varijabla koja naznauje je li kalendar rastavljen na vie stranica. \r\n\r\nlist_loop\r\nPetlja u kojoj se nalazi izlistan popis stavaka u kalendaru dogaaja.\r\n\r\nlist.date\r\nDatum dogaaja. \r\n\r\nlist.title\r\nNaziv dogaaja. \r\n\r\nlist.description\r\nOpis dogaaja. \r\n\r\nlist.sameAsPrevious\r\nUvjetna varijabla koja naznauje da je trenutni dogaaj istog datuma kao i posljednji dogaaj.\r\n\r\nlist.url\r\nURL na punu stranicu dogaaja.\r\n\r\nlist.controls\r\nUpravljake WebGUI kontrole za dogaaj.\r\n\r\nlist.firstPage\r\nLink na prvu stranicu. \r\n\r\nlist.lastPage\r\nLink na posljednju stranicu. \r\n\r\nlist.nextPage\r\nLink na sljedeu stranicu.\r\n\r\nlist.previousPage\r\nLink na prethodnu stranicu.\r\n\r\nlist.pageList\r\nPopis svih linkova za dogaaj.\r\n\r\nlist.multiplePages\r\nUvjetna varijabla koja naznauje postojanje vie stranica u popisu dogaaja.',1059215196,NULL);
