INSERT INTO international VALUES (73,'SyndicatedContent',3,'De volgende variabelen zijn beschikbaar voor het syndicated content sjabloon.\r\n\r\n<p>\r\n\r\n<b>channel.title</b><br>\r\nDe titel van dit stuk syndicated content.\r\n</p><p>\r\n\r\n<b>channel.description</b><br>\r\nEen beschrijving van de content beschikbaar via dit kanaal.\r\n</p><p>\r\n\r\n<b>channel.link</b><br>\r\nEen URL die terugverwijst naar de site van dit kanaal.\r\n</p><p>\r\n\r\n<b>item_loop</b><br>\r\nEen loop met de gegevens van dit kanaal.\r\nA loop containing the data from this channel.\r\n</p><blockquote>\r\n\r\n<b>title</b><br>\r\nDe titel van een gedeelte van de content.\r\n<p>\r\n\r\n<b>description</b><br>\r\nDe beschrijving van de content.\r\n</p><p>\r\n\r\n<b>link</b>\r\nEen URL direct naar de orginele content.',1056310030,NULL);
