INSERT INTO international VALUES (939,'WebGUI',3,'Als u een thema verwijdert dat u heeft gemaakt, verwijdert u enkel de basiseigenschappen van dit thema.\r\nAls u echter een thema verwijdert dat u heeft ge&iuml;mporteerd, verwijdert u ook al de componenten (stijlen, sjablone, collateral) die hier bij horen.\r\nWees dus zeker dat u geen van deze componenten nog in gebruik heeft voordat u een thema verwijdert. \r\n',1056214727,NULL);
