INSERT INTO international VALUES (71,'MessageBoard',16,'Fora dyskusyjne to najczciej miejsca wymiany pogldw i informacji pomidzy uytkownikami danego serwisu internetowego. Wiele organizacji i przedsibiorstw uywa ich rwnie na stronach swoich sieci wewntrznych - Intranetw, w celu usprawnienia wsppracy nad wasnymi projektami i przedsiwziciami.\r\n<br><br>',1066584548,NULL);
