INSERT INTO international VALUES (1063,'WebGUI',16,'<p>W szablonach powiadomie na forum dostpne s do wykorzystania nastpujce zmienne:\n\n</p><p>\n<b>notify.subscription.message</b><br>\nTre wiadomoci informujcej, e uytkownik otrzyma niniejsz wiadomo poniewa dokona wczeniej subskrypcji tego forum lub wtku.\n</p><p>\n\n<b>Uwaga:</b>W szablonie powiadomie mona rwnie stosowa zmienne dostpne w szablonie wiadomoci (wpisu).\n</p>',1066580520,NULL);
