INSERT INTO international VALUES (74,'Poll',16,'<p>W szablonach Sond dostpne s do wykorzystania nastpujce zmienne:\r\n</p><p>\r\n<b>canVote</b><br>\r\nWarunek okrelajcy czy dany uytkownik ma prawo uczestniczenia w danej sondzie.\r\n</p><p>\r\n\r\n<b>question</b><br>\r\nPytanie danej sondy, na ktr uytkownicy bd wybierali zdefiniowane odpowiedzi.\r\n</p><p>\r\n\r\n<b>form.start</b><br>\r\nPocztek formularza zawierajcego tre sondy.\r\n</p><p>\r\n\r\n<b>answer_loop</b><br>\r\nLista zawierajca wybr odpowiedzi na dan sond.\r\n</p><p>\r\n\r\n<blockquote>\r\n\r\n<b>answer.form</b><br>\r\nPrzycisk wyboru danej odpowiedzi sondy.\r\n</p><p>\r\n\r\n<b>answer.text</b><br>\r\nTre danej odpowiedzi sondy.\r\n</p><p>\r\n\r\n<b>answer.number</b><br>\r\nNumer porzdkowy danej odpowiedzi sondy (kolejne liczby cakowite - 1, 2, 3, itd.).\r\n</p><p>\r\n\r\n<b>answer.graphWidth</b><br>\r\nDugo graficznej belki reprezentujcej procentowy udzia danej odpowiedzi w gosowaniach uytkownikw. \r\n</p><p>\r\n\r\n\r\n<b>answer.percent</b><br>\r\nLiczba reprezentujca procentowy udzia danej odpowiedzi w gosowaniach uytkownikw.\r\n</p><p>\r\n\r\n<b>answer.total</b><br>\r\nSumaryczna liczba wszystkich gosowa uytkownikw.\r\n</p><p>\r\n\r\n</blockquote>\r\n\r\n\r\n<b>form.submit</b><br>\r\nPrzycisk umoliwiajcy zagosowanie w ramach danej sondy.\r\n</p><p>\r\n\r\n<b>form.end</b><br>\r\nKoniec formularza zawierajcego tre sondy.\r\n</p><p>\r\n\r\n<b>responses.label</b><br>\r\nTre etykiety opisujcej liczb wszystkich gosowa uytkownikw w danej sondzie.\r\n</p><p>\r\n\r\n<b>responses.total</b><br>\r\nLiczba wszystkich gosowa uytkownikw w danej sondzie.\r\n</p>',1050183668,NULL);
