INSERT INTO international VALUES (73,'EventsCalendar',1,'<b>Title</b><br>\r\nThe title for this event.\r\n<p>\r\n\r\n<b>Description</b><br>\r\nDescribe the activities of this event or information about where the event is to be held.\r\n<p>\r\n\r\n<b>Start Date</b><br>\r\nOn what date will this event begin?\r\n<p>\r\n\r\n<b>End Date</b><br>\r\nOn what date will this event end?\r\n<p>\r\n\r\n<b>Recurs every<b><br>\r\nSelect a recurrence interval for this event. \r\n\r\n<p>\r\n\r\n<b>What next?</b><br>\r\nSelect \"add new event\" if you\'d like to add another event, otherwise select \"go back to page\".\r\n<p>\r\n',1038887363,NULL);
INSERT INTO international VALUES (72,'FAQ',1,'Question, Add/Edit',1038890062,NULL);
INSERT INTO international VALUES (73,'FAQ',1,'<b>Question</b><br>\r\nAdd the question you\'d like to appear on the FAQ.\r\n<p>\r\n\r\n\r\n<b>Answer</b><br>\r\nAdd the answer for the question you entered above.\r\n<p>\r\n\r\n\r\n<b>What next?</b><br>\r\nIf you wish to add another question then leave this set to the default.\r\n\r\n<p>\r\n\r\n',1038890062,NULL);
INSERT INTO international VALUES (50,'Product',1,'Benefits are typically the result of the features of your product. They are why your product is so good. If you add benefits, you may also wish to consider adding some features.\r\n<p>\r\n\r\n<b>Benefit</b><br>\r\nYou may enter a new benefit, or select from one you\'ve already entered.\r\n<p>\r\n\r\n<b>Add another benefit?</b><br>\r\nIf you\'d like to add another benefit right away, select \"Yes\".\r\n<p>\r\n',1031514049,NULL);
INSERT INTO international VALUES (72,'LinkList',1,'Link, Add/Edit',1038887880,NULL);
INSERT INTO international VALUES (73,'LinkList',1,'<b>Title</b><br>\r\nThe text that will be linked.\r\n<p>\r\n\r\n<b>URL</b><br>\r\nThe web site to link to.\r\n<p>\r\n\r\n<b>Open in new window?</b><br>\r\nSelect yes if you\'d like this link to pop-up into a new window.\r\n<p>\r\n\r\n<b>Description</b><br>\r\nDescribe the site you\'re linking to. You can omit this if you\'d like.\r\n<p>\r\n\r\n<b>What Next?</b>\r\nLeave this set to the default if you wish to add another link after this one.\r\n<p>\r\n',1038887880,NULL);
INSERT INTO international VALUES (49,'Product',1,'Product Benefit, Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (697,'WebGUI',1,'Karma, Using',1031514049,NULL);
INSERT INTO international VALUES (698,'WebGUI',1,'Karma is a method of tracking the activity of your users, and potentially rewarding or punishing them for their level of activity. Once karma has been enabled, you\'ll notice that the menus of many things in WebGUI change to reflect karma.\r\n<p>\r\n\r\nYou can track whether users are logging in, and how much they contribute to your site. And you can allow them access to additional features by the level of their karma.\r\n<p>\r\n\r\nYou can find out more about karma in <a href=\"http://www.plainblack.com/ruling_webgui\">Ruling WebGUI</a>.',1031514049,NULL);
INSERT INTO international VALUES (5,'WobjectProxy',1,'Wobject Proxy, Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (6,'WobjectProxy',1,'With the Wobject Proxy (aka Shortcut) you can mirror a wobject from another page to any other page. This is useful if you want to reuse the same content in multiple sections of your site.\r\n<p>\r\n\r\n<b>NOTE:</b> The wobject proxy is not available through the Add Content menu, but instead through the shortcut icon on each wobject\'s toolbar.\r\n<p>\r\n\r\n<b>Wobject To Proxy</b><br>\r\nProvides a link to the orignal wobject being proxied.\r\n<p>\r\n\r\n<b>Override title?</b><br>\r\nSet to \"yes\" to use the title of the wobject proxy instead of the original title of the wobject.\r\n<p>\r\n\r\n<b>Override description?</b><br>\r\nSet to \"yes\" to use the description of the wobject proxy instead of the original description of the wobject.\r\n<p>\r\n\r\n<b>Override display title?</b><br>\r\nSet to \"yes\" to use the display title setting of the wobject proxy instead of the original display title setting of the wobject.\r\n<p>\r\n\r\n<b>Override template?</b><br>\r\nSet to \"yes\" to use the template of the wobject proxy instead of the original template of the wobject.\r\n<p>\r\n',1057091098,NULL);
INSERT INTO international VALUES (38,'Product',1,'Product, Add/Edit',1038889846,NULL);
INSERT INTO international VALUES (39,'Product',1,'WebGUI has a product management system built in to enable you to publish your products and services to your site quickly and easily.\r\n<p>\r\n\r\n<b>Template</b><br/>\r\nSelect a layout for this product.\r\n<p/>\r\n\r\n<b>Price</b><br>\r\nThe price of this product. You may optionally enter text like \"call for pricing\" if you wish, or you may leave it blank.\r\n<p>\r\n\r\n<b>Product Number</b><br>\r\nThe product number, SKU, ISBN, or other identifier for this product.\r\n<p>\r\n\r\n<b>Product Image 1</b><br>\r\nAn image of this product.\r\n<p>\r\n\r\n<b>Product Image 2</b><br>\r\nAn image of this product.\r\n<p>\r\n\r\n<b>Product Image 3</b><br>\r\nAn image of this product.\r\n<p>\r\n\r\n<b>Brochure</b><br>\r\nThe brochure for this product.\r\n<p>\r\n\r\n<b>Manual</b><br>\r\nThe product, user, or service manual for this product.\r\n<p>\r\n\r\n<b>Warranty</b><br>\r\nThe warranty for this product.\r\n<p>\r\n',1038889846,NULL);
INSERT INTO international VALUES (40,'Product',1,'Product Feature, Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (41,'Product',1,'Features are selling points for a product. IE: Reasons to buy your product. Features often result in benefits, so you may want to also add some benefits to this product.\r\n<p>\r\n\r\n<b>Feature</b><br>\r\nYou may enter a new feature, or select one you entered for another product in the system.\r\n<p>\r\n\r\n<b>Add another feature?</b><br>\r\nIf you\'d like to add another feature right away, select \"Yes\".\r\n<p>\r\n',1031514049,NULL);
INSERT INTO international VALUES (42,'Product',1,'Product Specification, Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (43,'Product',1,'Specifications are the technical details of your product.\r\n<p>\r\n\r\n\r\n<b>Label</b><br>\r\nThe type of specification. For instance, height, weight,   or color. You may select one you\'ve entered for another product, or type in a new specification.\r\n<p>\r\n\r\n\r\n<b>Specification</b><br>\r\nThe actual specification value. For instance, if you chose height as the Label, then you\'d enter a numeric value like \"18\".\r\n<p>\r\n\r\n\r\n<b>Units</b><br>\r\nThe unit of measurement for this specification. For instance, if you chose height for your label, perhaps the units would be \"meters\".\r\n<p>\r\n\r\n\r\n<b>Add another specification?</b><br>\r\nIf you\'d like to add another specification, select \"Yes\".\r\n<p>\r\n\r\n',1031514049,NULL);
INSERT INTO international VALUES (44,'Product',1,'Product Accessory, Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (45,'Product',1,'Accessories are products that enhance other products.\r\n<p>\r\n\r\n<b>Accessory</b><br>\r\nChoose from the list of products you\'ve already entered.\r\n<p>\r\n\r\n<b>Add another accessory?</b><br>\r\nSelect \"Yes\" if you have another accessory to add.\r\n<p>\r\n',1031514049,NULL);
INSERT INTO international VALUES (46,'Product',1,'Product (Related), Add/Edit',1031514049,NULL);
INSERT INTO international VALUES (47,'Product',1,'Related products are products that are comparable or complementary to other products.\r\n<p>\r\n\r\n\r\n<b>Related products</b><br>\r\nChoose from the list of products you\'ve already entered.\r\n<p>\r\n\r\n\r\n<b>Add another related product?</b><br>\r\nSelect \"Yes\" if you have another related product to add.\r\n<p>\r\n\r\n',1041876679,NULL);
