INSERT INTO international VALUES (637,'WebGUI',2,'<p><B>Vorname</B><br>\r\nDer Vorname des Benutzers.</p>\r\n<p><B>Zweiter Vorname</B><br>\r\nEin weiterer Vorname des Benutzers.</p>\r\n<p><B>Nachname</B><br>\r\nDer Nachname des Benutzers.</p>\r\n<p><B>Email Adresse</B><br>\r\nDie Emailadresse des Benutzers. Diese muss nur angegeben werden, wenn der Benutzer Funktionen nutzen möchte, die eine Emailadresse benötigen.</p>\r\n<p><B>ICQ UIN</B><br>\r\nDie <A href=\"http://www.icq.com/\">ICQ</A> UIN ist die &quot;User ID Number&quot; im ICQ Network. ICQ ist eine sehr bekannte Messaging Plattform.</p>\r\n<p><B>AIM Id</B><br>\r\nDie Account Id für den <A href=\"http://www.aim.com/\">AOL Instant Messenger</A> .</p>\r\n<p><B>MSN Messenger Id</B><br>\r\nDie Account Id für das <A \r\nhref=\"http://messenger.msn.com/\">Microsoft Network Instant Messenger system.</a></p>\r\n<p><B>Yahoo® Messenger Id</B><br>\r\nDie Account Id für den <A href=\"http://messenger.yahoo.com/\">Yahoo® Instant Messenger</A> system.</p>\r\n<p><B>Mobiltelefon</B><br>\r\nDie Mobilfunknummer des Benutzers.</p>\r\n<p><B>Pager</B><br>\r\nDie Pager Telefonnummer.</p>\r\n<p><B>Email zu Pager Gateway</B><br>\r\nDie Pager Email Adresse.</p>\r\n<p><B>Private Angaben</B><br>\r\nDie privaten Adressangaben.</p>\r\n<p><B>Geschäftliche Angaben</B><br>\r\nDie geschäftlichen Adressangaben.</p>\r\n<p><B>Geschlecht</B><br>\r\nDas Geschlecht des Benutzers.</p>\r\n<p><B>Geburtsdatum</B><br>\r\nDas Geburtsdatum des Benutzers.</p>\r\n<p>\r\n<B>Sprache</B><br>\r\nIn welcher Sprache sollen systembedingte Meldungen angezeigt werden?.</p>\r\n<p><B>Zeitabweichung</B><br>\r\nDie Anzahl der Stunden (plus oder minus), die die Zeit des Benutzers von der Serverzeit abweicht. Dies wird benutzt, um die einzelnen Zeitzonen anzugleichen.</p>\r\n<p><B>Erster Tag der Woche</B><br>\r\nDer erste Wochentag im Kalender. Zum Beispiel ist in den USA der erste Wochentag der Sonntag und in Deutschland ist es der Montag.</p>\r\n<p><B>Datumsformat</B><br>\r\nIn welchem Format soll das Datum erscheinen?</p>\r\n<p><B>Zeitformat</B><br>\r\nIn welchem Format soll die Zeit angezeigt werden?</p>\r\n<p><B>Diskussions Layout</B><br>\r\nSollen Diskussionen in einer flachen oder beitragsbasierten Struktur angezeigt werden? In einer flachen Struktur werden die Beiträge in der Reihenfolge, wie sie angelegt wurden, angezeigt. In der beitragsbasierten Struktur wird eine hierarchische Liste der Antworten, so wie sie erstellt wurden, angezeigt.</p>\r\n<p><B>Benachrichtigungen</B><br>\r\nWie soll der Benutzer über den Eingang neuer Nachrichten informiert werden?</p>',1067290755,NULL);
