INSERT INTO international VALUES (50,'Product',3,'Voordelen zijn het resultaat van kenmerken van het produkt. Ze beschrijven\r\nwaarom het produkt zo goed is. Als er voordelen worden toegevoegd, kan er\r\noverwogen worden om ook de kenmerken toe te voegen.<br>\r\n<br>\r\n<b>Voordeel</b><br>\r\nEr kan een nieuw voordeel ingevoerd worden of een voordeel gekozen worden\r\nvan degene die al ingevoerd zijn.<br>\r\n<br>\r\n<b>Nog een voordeel toevoegen?</b><br>\r\nAls er meteen nog een voordeel toegevoegd gaat worden, selecteer dan \"Ja\".<br>\r\n',1037891794,NULL);
