INSERT INTO international VALUES (4,'Survey',10,'Undersøgelser lader dig indsamle informationer fra brugerne. I WebGUI\'s tilfælde kan du også bruge systemet til at teste dine brugers viden.\r\n\r\n<p>\r\n<bold>Spørgsmåls rækkefølge</bold>\r\nRækkefølgen for spørgsmålene.\r\n<i>Sequential</i> viser spørgsmålene i den rækkefølge du har opettet dem i. <br>\r\n<i>tilfældig rækkefølge</i> viser spørgsmålene i tilfældig rækkefølge.<br>\r\n<i>Respons styret</i> viser spørgsmålene i en rækkefølge der afhænger af svarene fra brugerne.\r\n\r\n<p>\r\n<bold>Metode</bold>\r\nSom udgangspunkt anvendes <i>undersøgelses</i> metoden. Denne stiller spørgsmål til brugerne som du får summeret op. Denne metode er til f.eks. brugerundersøgelser.\r\nI <i>Quiz</i> metode kan brugerne se det rigtige svar efterfølgende, og får oplyst hvor mange rigtige svar de havde. Denne metode er til test og lign.\r\n\r\n<p>\r\n<bold>Hvem kan deltage?</bold!\n>\r\nHer angiver du hvem der kan deltage.\r\n\r\n<p>\r\n<bold>Hvem kan se rapporter</bold>\r\nHer angiver du hvem der kan se resultatet.\r\n',1046851517,NULL);
