INSERT INTO international VALUES (838,'WebGUI',8,'Le Cartelle sono usate per organizzare i contenuti dell\'Archivio Collaterale,\r\nnello stesso modo in cui organizzi i file sul tuo hard disk.\r\n<p><b>Organizza in Cartelle</b><br>\r\nLe cartelle possono stare all\'interno di altre cartelle.\r\nIn quale cartella vuoi inserire questa cartella?\r\n<p><b>Name</b><br>\r\nDai un nome a questa cartella così puoi riconoscere cosa contiene.\r\n<p><b>Descrizione</b><br>\r\nDescrivi la cartella in modo da ricordare perché è stata creata e che cosa\r\ndovrebbe contenere.</p>',1045775049,NULL);
