INSERT INTO international VALUES (89,'DataForm',3,'De volgende variabelen zijn beschikbaar voor het gegevensformulier lijst sjabloon:\r\n<p>\r\n\r\n<b>back.url</b><br>\r\nDe URL om terug te gaan naar de gegevensinvoerpagina van dit gegevensformulier.\r\n</p><p>\r\n\r\n<b>back.label</b><br>\r\nHet standaard label van back.url.\r\n</p><p>\r\n\r\n<b>field_loop</b><br>\r\nEen loop met informatie over de velden in dit gegevensformulier.\r\n</p><p =\"\"></p><blockquote>\r\n\r\n<b>field.name</b><br>\r\nDe webveilige naam van dit veld.\r\n<p>\r\n\r\n<b>field.label</b><br>\r\nHet leesbare label voor dit veld.\r\n</p><p>\r\n\r\n<b>field.id</b><br>\r\nEen unieke id voor dit veld in de database.\r\n</p><p>\r\n\r\n<b>field.isMailField</b><br>\r\nBepaalt of dit veld bestaat voor het mail subsysteem van het gegevensformulier.\r\n</p><p>\r\n\r\n<b>field.type</b><br>\r\nHet gegevenstype dat bij dit veld hoort.\r\n</p><p>\r\n\r\n</p></\n blockquote>\r\n\r\n<b>record_loop</b><br>\r\nEen loop met die record invoer bevat van dit gegevensformulier.\r\n<p>\r\n\r\n</p><blockquote>\r\n\r\n<b>record.entryId</b><br>\r\nEen uniek ID voor de invoer van deze record.\r\n<p>\r\n\r\n<b>record.ipAddress</b><br>\r\nHet IP-adres van de gebuiker die deze recor heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>record.edit.url</b><br>\r\nDe URL om dit record aan te passen.\r\n</p><p>\r\n\r\n<b>record.username</b><br>\r\nDe gerbruikersnaam van de persoon die deze record heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>record.userId</b><br>\r\nHet gebruikers ID van de persoon die deze record heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>record.submissionDate.epoch</b><br>\r\nDe datum in epochformaat voor de invoer van dit record.\r\n</p><p>\r\n\r\n<b>record.submissionDate.human</b><br>\r\nDe datum van deze recordinvoer in een normaal leesbaar formaat, zoals in de voorkeuren is ingevoerd voor deze gebruiker.\r\n</p><p>\r\n\r\n<b>record.data_loop</b><br>\r\nEen l\n oop met de gegevens die door deze gebruiker zijn ingevoerd voor elk veld van dit gegevensformulier.\r\n</p><p>\r\n\r\n</p><blockquote>\r\n\r\n<b>record.data.value</b><br>\r\nDe waarde zoals ingevoerd door de gebruiker voor dit veld in deze recordinvoer.\r\n<p>\r\n\r\n<b>record.data.name</b><br>\r\nDe webveilige naam van dit veld.\r\n</p><p>\r\n\r\n<b>record.data.label</b><br>\r\nDe normaal leesbare label voor dit veld.\r\n</p><p>\r\n\r\n<b>record.data.isMailField</b><br>\r\nBepaalt of dit veld bestaat voor het mail subsysteem van dit gegevensformulier.\r\n</p>',1056309770,NULL);
INSERT INTO international VALUES (83,'DataForm',3,'De volgende sjabloon variabelen zijn beschikbaar voor Data Formulier Sjablonen\r\n<p>\r\n\r\n<b>acknowledgement</b><br>\r\nDe erkenning (acknowledgement) zoals gespecificeerd in een wobjects eigenschappen. Dit bericht behoort weergegeven te worden als een gebruiker gegevens invoert.\r\n</p><p>\r\n<b>export.tab.url</b><br>\r\nAls op deze URL geklikt wordt worden de gegevevens die zijn opgeslagen in dit dataformulier ge&euml;xporteerd als een zogenaamde tab delimited file.\r\n</p><p>\r\n\r\n<b>export.tab.label</b><br>\r\nHet standaard label van de export.tab.url variabele.\r\n</p><p>\r\n\r\n<b>entryList.url</b><br>\r\nOp deze link klikken laat de volledige lijst van ingevoerde records in dit dataformulier zien.\r\n</p><p>\r\n\r\n<b>entryList.label</b><br>\r\nHet standaard labe voor de entryList.url variabele.\r\n</p><p>\r\n\r\n<b>canEdit</b>\r\nDit bepaalt of de huidige\n  gebruiker het recht heeft om een bestaande invoer bewerken of de gegevens van het formulier te exporteren.\r\n</p><p>\r\n\r\n<b>back.url</b><br>\r\nEen url dat u terug brengt naar de standaard pagina in dit formulier.\r\n</p><p>\r\n\r\n<b>back.label</b><br>\r\nHet standaard label voor de back.url variabele. \r\n</p><p>\r\n\r\n<b>username</b>*<br>\r\nDe gebruikersnaam van de gebruiker die de gegevens heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>userId</b>*<br>\r\nHet gebruikers ID van de gebruiker die de gegevens heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>date</b>*<br>\r\nDe datum dat deze gegevens zijn ingevoerd of voor het laatst zijn ververst in het door de gebuiker geprefereerde datum/tijd formaat.\r\n</p><p>\r\n\r\n\r\n<b>epoch</b>*<br>\r\nDe datum dat deze gegevens zijn ingevoerd of voor het laatst zijn ververst in een epoch formaat.\r\n</p><p>\r\n\r\n<b>ipAddress</b>*<br>\r\nHet IP-adres van de gebruiker dat de gegevens heeft ingevoerd.\r\n</p><p>\r\n\r\n<b>edit.url</b>*<br>\r\nD\n e URL van de pagina om deze invoer te bewerken.\r\n</p><p>\r\n\r\n<b>error_loop</b>*<br>\r\nEen \'loop\' die fout informatie bevat, bijvoorbeeld als iemand een verplicht veld niet heeft ingevuld.\r\n</p><p>\r\n\r\n</p><blockquote>\r\n\r\n<b>error.message</b>*<br>\r\nEen foutmelding die aangeeft wat de gebruiker mogelijk fout heeft gedaan.\r\n\r\n</blockquote>\r\n\r\n<b>addField.url</b><br>\r\nDe URL die door content managers wordt gebruikt om een nieuw veld aan het formulier toe te voegen.\r\n<p>\r\n\r\n<b>addField.label</b><br>\r\nHet standaard labele voor de addField.url variabele.\r\n</p><p>\r\n\r\n<b>form.start</b><br>\r\nHet begin van het formulier.\r\n</p><p>\r\n\r\n<b>field_loop</b><br>\r\nEen loop met al de veld informatie.\r\n</p><p>\r\n\r\n</p><blockquote>\r\n\r\n<b>field.form</b><br>\r\nHet formulier element voor dit veld.\r\n<p>\r\n\r\n<b>field.name</b><br>\r\nDe naam van dit veld.\r\n</p><p>\r\n\r\n<b>field.value</b><br>\r\nDe waarde van dit veld. Als dit nieuwe\n  dat is dan wordt hier de standaard waarde gebruikt.\r\n</p><p>\r\n\r\n<b>field.label</b><br>\r\nDe tekst label voor dit veld.\r\n</p><p>\r\n\r\n<b>field.isHidden</b><br>\r\nDit bepaalt of dit veld verborgen behoort te zijn.\r\n</p><p>\r\n\r\n<b>field.isDisplayed</b><br>\r\nBepaalt of dit veld weergegeven behoort te worden.\r\n</p><p>\r\n\r\n<b>field.isEditable</b><br>\r\nBepaalt of dit veld te bewerken is.\r\n</p><p>\r\n\r\n<b>field.isRequired</b><br>\r\nBepaalt of dit veld verplicht is.\r\n</p><p>\r\n\r\n<b>field.isMailField</b><br>\r\nBepaalt of dit veld alleen aanwezig is om het mogelijk te maken een email te versturen.\r\n</p><p>\r\n\r\n\r\n<b>field.subtext</b><br>\r\nEen beschrijving van het veld, om gebruikers te laten weten wat ze in dit veld behoren in te vullen.\r\n</p><p>\r\n\r\n<b>field.controls</b><br>\r\nWebGUI\'s beheerders mogelijkheden voor dit veld.\r\n</p><p>\r\n\r\n</p></blockquote>\r\n\r\n<b>form.send</b><br>\r\nEen forumulier knop met het woordb \"send\n \" erin.\r\n<p>\r\n\r\n<b>form.save</b&gt;<br>\r\nEen formulierknop met het woord \"save\" erin.\r\n</b></p><p>\r\n\r\n<b>form.end</b><br>\r\nHet einde van het formulier.\r\n</p><p>\r\n\r\n<b>*Alleen beschikbaar als de gebuiker het formulier al heeft ingevoerd.</b>',1056308952,NULL);
