INSERT INTO international VALUES (934,'WebGUI',3,'Het maken en bewerken van themas is vrij eenvoudig. Eerst dient u een paar basiseigenschappen voor het thema opgeven.\r\nVervolgens start u met het toevoegen van componenten aan het thema. \r\n<p>\r\nDe basis eigenschappen van een thema kunnen niet veranderd worden, anders dan op de site waar het thema is gemaakt.\r\nDe volgende definities zijn er van de basiseigenschappen van een thema:</p><p>\r\n<b>Thema ID</b><br>\r\nHet unieke ID van dit thema binnen deze WebGUI site. Dit ID zal veranderen als dit thema is ge&iuml;mporteerd in een andere site.\r\n</p><p>\r\n\r\n<b>Thema naam</b><br> \r\nDit is de unieke naam van dit thema. Het moet uniek zijn in elke site waarin dit thema ge&iuml;mporteerd wordt.\r\nDeze naam verandert niet in alle sites. </p><p>\r\n\r\n<b>Thema ontwerper</b><br>\r\nDe naam van de persoon of het bedrijf dat het thema heeft gecre&euml;erd\r\n\r\n\r\n n</p><p>\r\n\r\n<b>Ontwerper URL</b><br>\r\n\r\nDe URL van de website van de ontwerper van dit thema. Als u zakelijk WebGUI sites maakt is dit de plaats om reclame voor uw werk te maken.\r\n</p><p>\r\n\r\n</p>',1056213741,NULL);
INSERT INTO international VALUES (933,'WebGUI',3,'Thema, bewerk',1056213108,NULL);
INSERT INTO international VALUES (932,'WebGUI',3,'Themas vormen een mechanisme waarmee u snel nieuwe stijlen, sjablonen en collateral in WebGUI kunt importeren. \r\nThemas zijn ook erg handig om items van de ene site naar een andere te verplaatsen. <p>\r\n\r\n<b>TIP:</b>\r\nAls u een thema maakt, benoem de componenten (stijlen, sjablonen, collateral) die u in dit thema gebruikt dan zodanig dat \r\ndat ze duidelijk bij dit thema horen. Dit is handig omdat gebruikers dan eenvoudig die componenten in de stijl kunnen vinden en het voorkomt problemen door dubbele namen.\r\n</p>\r\n',1056213085,NULL);
