INSERT INTO international VALUES (74,'Poll',3,'De volgende variabelen zijn beschikbaar bij de peiling sjabloon:<br>\r\n<br>\r\n<b>canVote</b><br>\r\nEen conditie die aangeeft of de gebruiker het recht heeft te stemmen in deze peiling.\r\n<p>\r\n\r\n<b>question</b><br>\r\nDe peiling vraag.\r\n</p><p>\r\n\r\n<b>form.start</b><br>\r\nHet begin van het peilingsformulier.\r\n</p><p>\r\n\r\n<b>answer_loop</b><br>\r\nEen loop die informatie bevat ocer de antwoorden in de peiling.\r\n</p><p>\r\n\r\n</p><blockquote>\r\n\r\n<b>answer.form</b><br>\r\nRadio button for dit antwoord.\r\n<p>\r\n\r\n<b>answer.text</b><br>\r\nDe tekst van dit antwoord.\r\n</p><p>\r\n\r\n<b>answer.number</b><br>\r\nHet nummer van dit antwoord (zoals 1, 2, 3 enz.)\r\n</p><p>\r\n\r\n<b>answer.graphWidth</b><br>\r\nDe breedte van de grafiek van dit antwoord. Het is gebaseerd op een percentage van de totale grootte van de grafiek.\r\n</p><p>\r\n\r\n\r\n<b>\n answer.percent</b><br>\r\nHet percentage van de peiling dat dit antwoord tot zover heeft gekregen.\r\n</p><p>\r\n\r\n<b>answer.total</b><br>\r\nHet totaal aantal stemmen dat dit antwoord heeft gekregen.\r\n\r\n</p><p>\r\n\r\n</p></blockquote>\r\n\r\n\r\n<b>form.submit</b><br>\r\nDe invoerknop van het formulier van deze peiling.\r\n<p>\r\n\r\n<b>form.end</b><br>\r\nHet einde van het formulier van deze peiling.\r\n</p><p>\r\n\r\n<b>responses.label</b><br>\r\nDe naam (label) voor het totaal aantal reacties. \"Totaal stemmen\"\r\n</p><p>\r\n\r\n<b>responses.total</b><br>\r\nHet totaal van het aantal stemmen die zijn uitgebracht in deze stemming.\r\n</p><p>\r\n\r\n</p>',1056211690,NULL);
