insert into international (internationalId,languageId,namespace,message,lastUpdated) values (651,3,'WebGUI','Bij het legen van de prullenbak wordt alles wat erin zit voor altijd weggegooid. Voor zaken waar u niet zeker van bent of ze wel weggegooid moeten kunt u het beste even in het klemboord knippen voordat u de prullenbak leegt.', 1038483463);
