insert into international (internationalId,languageId,namespace,message,lastUpdated) values (633,3,'WebGUI','Simpel gezegd zijn \'roots\' pagina\'s zonder hogerliggende pagina. De eerste en belangrijkste \'root\' in WebGUI is de \"Home\" pagina. In de meeste gevallen is een extra \'root\' nooit nodig. Voorbeelden voor een extra \'root\' zijn een verborgen pagina voor administratieve redenen of een nieuwe \'root\' om de zoekmachine op te plaatsen.', 1038483226);
delete from international where languageId=3 and namespace='WebGUI' and internationalId=630;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (630,3,'WebGUI','WebGUI heeft een kleine, maar degelijke real-time zoekmachine ingebouwd.\r\nWanneer u de zoemachine wilt gebruiken kunt u het ^?; macro toepassen, u\r\nkunt ook ?op=search aan het eind van een URL toevoegen of een apart formulier\r\nbouwen om het aan te spreken.<br>\r\n<br>\r\nVeel mensen hebben een specifieke zoekmachine nodig om hun WebGUI site en\r\nenkele anderen te indexeren. In dat geval raden we <a\r\n href=\"http://www.mnogosearch.org/\">MnoGo Search</a> of <a\r\n href=\"http://www.htdig.org/\">ht://Dig</a> aan. <br>\r\n', 1038483077);
delete from international where languageId=3 and namespace='WebGUI' and internationalId=629;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (629,3,'WebGUI','<b>Voorkom Proxy Caching</b><br>\r\nSommige bedrijven hebben proxyservers die problemen met WebGUI kunnen opleveren.\r\nWanneer er problemen met WebGUI geconstateerd worden en er wordt tevens gebruik\r\ngemaakt van een proxyserver is het wellicht verstandig deze instelling op\r\nJa te zetten.<br>\r\n<i>Let op: </i>WebGUI\'s URLs zullen er niet zo gebruikersvriendelijk uit\r\nzien wanneer deze optie aan staat<br>\r\n<br>\r\n<b>Laat debugging zien</b><br>\r\nWanneer deze optie aan staat worden alle variabelen die gebruikt worden door\r\nWebGUI onderaan uw pagina getoond.<br>\r\n<i>Let op:</i> Wanneer debugging aan staat zijn veel security gevoelige gegevens\r\nvoor iedereen te zien zoals het wachtwoord van uw SQL database. Gebruik deze\r\nsetting dan ook alleen op ontwikkelservers.<br>\r\n<br>\r\nHoudt pagina statistieken bij?<br>\r\nDeze optie zet het verzamelen van pagina statistieken aan. Het genereert\r\nwat extra belasting op uw database en processor maar geeft uw inzicht in\r\nhoe vaak pagina\'s gebruikt worden.<br>\r\n', 1038482562);
