insert into international (internationalId,languageId,namespace,message,lastUpdated) values (625,3,'WebGUI','<b>Naam<br>\r\n</b>Het label waaraan naar dit plaatje gerefereerd kan worden wanneer het\r\nin pagina\'s geplaatst wordt met ^I(\"label\");<br>\r\n<br>\r\n<b>Bestand</b><br>\r\nKies een bestand van je lokale harddisk om op de server te zetten.<br>\r\n<br>\r\n<b>Parameters</b><br>\r\nHier kunnen HTML &lt;img&gt; parameters toegevoegd worden die voor dit plaatje\r\ngebruikt zullen worden.<br>\r\n<br>\r\n<i>Voorbeelden:</i><br>\r\nalign=\"right\"<br>\r\nalt=\"Beschrijving voor dit plaatje\"<br>\r\nwidth=\"300\" (breedte in pixels)<br>\r\nheight=\"200\" (hoogte in pixels)<br>\r\n', 1038480870);
