insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'ExtraColumn','Extra kolommen geven je de mogelijkheid om de layout van de pagina voor een\r\nspecifieke pagina aan te passen. Meer mogelijkheden hiertoe bieden de stijlen\r\nen sjablonen.<br>\r\n<br>\r\nKolommen worden altijd van links naar rechts toegevoegd. Reeds bestaande\r\ninhoud zal daarom links van de nieuwe kolom te zien zijn.<br>\r\n<br>\r\n<b>Tussenruimte<br>\r\n</b>Tussenruimte is de hoeveelheid ruimte in pixels tussen de reeds bestaande\r\ncontent en de nieuwe kolom.<br>\r\n<br>\r\n<b>Breedte</b><br>\r\nBreedte is de werkelijke breedte van de nieuwe kolom in pixels.<br>\r\n<br>\r\n<b>Style sheet klasse (class)</b><br>\r\nDe standaardklasse die normaal aan alle kolommen wordt toegekend is content\r\n(de stijl die de body van de site normaal gebruikt). Het is echter mogelijk\r\nom een speciale stijl voor de extra kolom te definieren.<br>\r\n', 1038480365);
