insert into international (internationalId,languageId,namespace,message,lastUpdated) values (614,3,'WebGUI','Stijlen worden gebruikt om te regelen he de WebGUI paginas eruit zien en\r\naanvoelen. Met WebGUI kan een onbeperkt aantal stijlen gemaakt worden, zodat\r\nde pagina zoveel uiterlijken kan hebben als gewenst. Er kunnen een paar paginas\r\ngemaakt worden die de stijl van de brochure hebben en een paar andere met\r\nhet uitelijk van&nbsp;Yahoo!&reg;. Er kunnen zelfs paginas gemaakt worden\r\nmet het uiterlijk van de paginas van een boek. Met stijl beheer is er onbeperkt\r\ninvloed op alle ontwerpen.<br>\r\n<br>\r\nEr zijn verscheidene stijlen ingebouwd in WebGUI. De eersten worden gebruikt\r\ndoor WebGUIen moeten niet worden bewerkt of verwijderd. De laatste paar zijn\r\nvoorbeeldstijlen en kunnen zoveel als gewenst worden bewerkt of verwijderd.<br>\r\n<br>\r\n<b>Klembord</b><br>\r\nDeze stijl wordt door het klembord systeem gebruikt.<br>\r\n<br>\r\n<b>Veilige modus</b><br>\r\nWanneer een stijl wordt verwijderd dat op sommige paginas nog steeds gebruikt\r\nwordt, zal de Veilige Modus stijl gebruikt worden op deze paginas. Deze stijl\r\nheeft een witte achtergrond en een simpele navigatie.<br>\r\n<br>\r\n<b>Maak Pagina Printbaar </b><br>\r\nDeze stijl wordt gebruikt als een <b>^r;</b> macro wordt geplaatst op de\r\npaginas en de gebruiker hierop klikt. Deze stijl stelt u in staat een simpel\r\nlogo en een copyright bericht op de printbare paginas af te drukken.<br>\r\n<br>\r\n<b>Pakketten</b><br>\r\nDeze stijl wordt gebruikt bij het pakketten beheer systeem.<br>\r\n<br>\r\n<b>Afval</b><br>\r\nDeze stijl wordt gebruikt door het afval systeem<br>\r\n<br>\r\n<hr width=\"100%\" size=\"1\" noshade=\"noshade\"><b>Demonstratie Stijl</b><br>\r\nDit is een proefontwerp dat van een sjablonenpagina (www.freewentemplates.com)\r\ngehaald.<br>\r\n<br>\r\n<b>Plain Black Software (black) &amp; (white)</b><br>\r\nDeze ontwerpen worden op de Plain Black paginas gebruikt.<br>\r\n<br>\r\n<b>Yahoo!&reg;</b><br>\r\nDit is is het ontwerp van de Yahoo!&reg; pagina (zonder toestemming gebruikt).<br>\r\n<br>\r\n<b>WebGUI</b><br>\r\nDit is een simpel ontwerp met de WebGUI logos.<br>\r\n<br>\r\n<b>WebGUI 4</b><br>\r\nDeze stijl was toegevoegd bij WebGUI 4.0.0. Het is nu de standaard stijl\r\nen heeft de WebGUI stijl overtroffen.<br>\r\n', 1037986626);
