insert into international (internationalId,languageId,namespace,message,lastUpdated) values (613,3,'WebGUI','Gebruikers zijn de accounts in het systeem die de rechten hebben om bepaalde\r\ndingen te doen. er zijn twee standaard gebruikers in het systeem: Beheerder\r\nen Bezoeker.<br>\r\n<br>\r\n<i><b>Beheerder</b><br>\r\nDe is precies wat het zegt. Het is een gebruiker met onbeperkte rechten in\r\nde WebGUI omgeving. Als iets gedaan kan worden, heeft deze gebruiker het\r\nrecht om het te doen.<br>\r\n<br>\r\n<br>\r\n<b>Bezoeker</b><br>\r\nDe Bezoeker is precies het tegenovergestelde van de Beheerder. een Bezoeker\r\nheeft geen rechten. Standaard wordt een gebruiker die niet ingelogd is gezien\r\nals Bezoeker.<br>\r\n<br>\r\n<br>\r\n<b>Voeg nieuwe gebruiker toe.</b><br>\r\nKlik hierop om naar het \"Gebruiker Toevoegen\" scherm te gaan.<br>\r\n<br>\r\n<br>\r\n<b>Zoek</b><br>\r\nGebruikers kunnen gezocht worden op gebruikersnaam en email adres. Er kunnen\r\nook gedeeltelijke zoekopdrachten uitgevoerd worden.</i><br>\r\n', 1037983861);
