insert into international (internationalId,languageId,namespace,message,lastUpdated) values (611,3,'WebGUI','<b>Bedrijfsnaam</b><br>\r\nDe naam van het bedrijf. Deze zal in alle emails en overal waar het bedrijfsnaam\r\nmacro wordt gebruikt verschijnen.<br>\r\n<br>\r\n<b>Bedrijfs Email Adres</b><br>\r\nEen algemeen email adres van het bedrijf. Dit is het adres waar alle geautomatiseerde\r\nemails van het bedrijf vandaan komen. Het kan ook gebruikt worden met behulp\r\nvan het WebGUI macro systeem.<br>\r\n<br>\r\n<b>Bedrijfs URL</b><br>\r\nHet primaire URL van het bedrijf. Dit zal in alle emails, die met het WebGUI\r\nsysteem verstuurd worden verschijnen. Het kan ook gebruikt worden met behulp\r\nvan het WebGUI macro systeem.<br>\r\n', 1037982683);
