insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'SiteMap','Pagina overzichten worden gebruikt om extra navigatie te bieden in WebGUI.\r\nEen traditioneel overzicht kan gemaakt worden dat een hierarchisch beeld\r\ngeeft van alle paginas in de webpagina. Ook kunnen pagian overzichten gebruikt\r\nworden om extra navigatie te bieden in een bepaalde niveauvan de pagina.<br>\r\n<br>\r\n<b>Afbeelden korte samenvatting?</b><br>\r\nIs het gewenst een pagina samenvatting te laten zien naast de links naar\r\nelke pagina? Let op dat om deze optie te valideren, korte samenvattingen\r\ngedefinieerd moeten zijn voor de paginas.<br>\r\n<br>\r\n<b>Vanaf dit niveau beginnen?</b><br>\r\nAls het pagina overzicht de pagina boom weergeeft vanaf het huidige niveau,\r\nvink dan dit vak aan. Vink het uit als de hele boom weergegeven moet worden.<br>\r\n<br>\r\n<b>Aantal niveaus weer te geven</b><br>\r\nHoeveel niveaus moet het pagina overzicht laten zien. Wanneer 0 is ingevoerd\r\nzullen alle onderliggende niveaus getoond worden.<br>\r\n<br>\r\n<b>Inspringen</b><br>\r\nHoeveel karakters moet elk niveau inspringen?<br>\r\n<br>\r\n<b>Opsommingsteken</b><br>\r\nSpecificeer welk opsommingsteken gebruikt moet worden voor elke regel. Dit\r\nkan leeggelaten worden. Ook kunn HTML opsomminstekens als <b>&middot;</b>\r\nen <b>&raquo;</b> gespecificeerd worden. Zelfs afbeeldingen uit de afbeeldingsmanager\r\nkunnen gebruikt worden door een macro op deze wijze te specificeren ^l(opsommingsteken);.<br>\r\n<br>\r\n<b>Regelafstand</b><br>\r\nSpecificeer het aantal enters dat tussen elk object in het overzicht moet\r\nstaan. Dit aantal moet op 1 of meer staan.<br>\r\n', 1037899763);
