insert into international (internationalId,languageId,namespace,message,lastUpdated) values (63,3,'Product','Produkt sjablonen controleren hoe een produkt gepresenteerd wordt aan een \r\nklant, in een vergelijkbare manier als dat andere sjablonen in WebGUI inhoud \r\nweergeven. Er zijn een paar sjablonen geleverd als referentie, maar er zou \r\neen sjabloon gemaakt moeten worden dat past bij het produkt.<br>\r\n <br>\r\n De kans bestaat dat niet alle velden van WebGUI\'s produkt manager nodig\r\nzijn. Voor snelle bediening kunnen de velden die niet gebruikt worden, verwijderd \r\nworden.<br>\r\n <br>\r\n <b>N.B.:</b> Maak een kopie van het standaard sjabloon in plaats van het \r\nstandaard sjabloon te bewerken. Dit is zodat het sjabloon niet verloren gaat \r\nbij het upgraden van WebGUI als het standaard sjabloon gewijzigd is. Wanneer \r\ner een kopie gemaakt is of een eigen sjabloon, is er geen risico dat de wijzigingen \r\nverloren gaan.<br>\r\n <br>\r\n Er zijn vele op maat gemaakte macros die gebruikt kunnen worden inde produkt \r\nsjablonen. Deze macros volgen hier:<br>\r\n <br>\r\n <b>^Produkt_Accessoires;</b><br>\r\n Laat de lijst van accesoires zijn die met dit produkt geassocieerd worden.<br>\r\n <br>\r\n <b>^Produkt_Voordelen;</b><br>\r\n Laat de lijst van voordelen van dit produkt zijn.<br>\r\n <br>\r\n <b>^Produkt_Beschrijving;</b><br>\r\n Laat de beschrijving van het produkt zien.<br>\r\n <br>\r\n <b>^Produkt_Kenmerken;</b><br>\r\n Laat de kenmerken van het produkt zien.<br>\r\n <br>\r\n <b>^Produkt_Afbeelding1;</b><br>\r\n Laat de eerste afbeelding die bij dit produkt is ingeladen (als deze er\r\nis).<br>\r\n <br>\r\n <b>^Produkt_Afbeelding2;</b><br>\r\n  Laat de tweede afbeelding die bij dit produkt is&nbsp;ingeladen (als deze\r\ner is).<br>\r\n <br>\r\n <b>^Produkt_Afbeelding3;</b><br>\r\n  Laat de derde afbeelding die bij dit produkt is&nbsp;ingeladen (als deze\r\ner is).<br>\r\n <br>\r\n<b>^Produkt_Nummer;</b><br>\r\nLaat het produkt nummer of SKU zien.<br>\r\n<br>\r\n<b>^Produkt_Prijs;</b><br>\r\nLaat de prijs van het produkt zien.<br>\r\n<br>\r\n<b>^Produkt_Gerelateerd;</b><br>\r\nLaat de produkten die gerelateerd zijn met dit produkt zien.<br>\r\n<br>\r\n<b>^Produkt_Specificaties;</b><br>\r\nLaat de lijst met specificaties van dit produkt zien<br>\r\n<br>\r\n<b>^Produkt_Miniatuur1;</b><br>\r\nLaat het miniatuur van de eerste afbeelding dat met dit produkt is ingeladen\r\n(als deze er is), samen met een link naar het volledig formaat van deze afbeelding.<br>\r\n<br>\r\n<b>^Produkt_Miniatuur2;</b><br>\r\n Laat het miniatuur van de tweede afbeelding dat met dit produkt is ingeladen \r\n(als deze er is), samen met een link naar het volledig formaat van deze afbeelding.<br>\r\n<br>\r\n<b>^Produkt_Miniatuur3;</b><br>\r\n Laat het miniatuur van de derde afbeelding dat met dit produkt is ingeladen \r\n(als deze er is), samen met een link naar het volledig formaat van deze afbeelding.<br>\r\n<br>\r\n<b>^Produkt_Titel;</b><br>\r\nLaat de titel van het produkt zien. Let op wanneer dit macro gebruikt wordt,\r\nhet waarschijnlijk gewenst is de standaard titel uit te zetten door \"Nee\"\r\nte selecteren in het \"titel weergeven\" veld.<br>\r\n', 1037894729);
