insert into international (internationalId,languageId,namespace,message,lastUpdated) values (39,3,'Product','WebGUI heeft een ingebouwd produkt management systeem, zodat produkten en\r\ndiensten snel en makkelijk gepubliceerd kunnen worden.<br>\r\n<br>\r\n<b>Prijs</b><br>\r\nDe prijs van het produkt. Hier kan optioneel \"Prijs op te vragen per telefoon\"\r\ningevuld worden, of de prijs kan weggelaten worden.<br>\r\n<br>\r\n<b>Produktnummer</b><br>\r\nHet produktnummer, SKU, ISBN of een andere indentificatie van dit produkt.<br>\r\n<br>\r\n<b>Afbeelding van het produkt 1</b><br>\r\nEen afbeelding van het produkt.<br>\r\n<br>\r\n<b>Afbeelding van het produkt 2</b><br>\r\n Een afbeelding van het produkt.<br>\r\n<br>\r\n<b>Afbeelding van het produkt 3</b><br>\r\n Een afbeelding van het produkt.<br>\r\n<br>\r\n<b>Brochure</b><br>\r\nDe brochure van dit produkt<br>\r\n<br>\r\n<b>Handleiding</b><br>\r\nDe produkt, gebruiks of onderhouds handleiding van dit produkt.<br>\r\n<br>\r\n<b>Garantie</b><br>\r\nDe Garantie van dit produkt.<br>\r\n', 1037888328);
