insert into international (internationalId,languageId,namespace,message,lastUpdated) values (72,3,'MailForm','Er kan een willekeurig aantal velden aan het mail formulier toegevoegd worden.<br>\r\n<br>\r\n<b>Veldnaam</b><br>\r\nDe naam van dit veld. Dit dient uniek te zijn voor alle velden op het formulier.<br>\r\n<br>\r\n<b>Status</b><br>\r\nVerborgen velden zijn niet zichtbaar voor de gebruiker, maar worden wel meegezonden\r\nin de email.<br>\r\nWeergegeven velden laat de gebruiker het veld zien, maar stelt de gebruiker\r\nniet in staat het veld aan te passen.<br>\r\nAanpasbare velden kunnen ingevuld worden door de gebruiker.<br>\r\nZorg dat een Standaardwaarde ingevuld word als hier voor een verborgen of\r\nweergegeven veld wordt gekozen.<br>\r\n<br>\r\n<b>Type</b><br>\r\nKies het type element voor dit veld. De volgende typen velden worden ondersteund:<br>\r\nURL: Een textveld dat automatisch ingevulde URL\'s &nbsp;opmaakt.<br>\r\nTekstveld: een standaard tekstveld.<br>\r\nDatum: Een tekstveld met een popup window waar een datum in geselecteerd\r\nkan worden.<br>\r\nJa/Nee: Een set van keuzeknoppen.<br>\r\nEmail adres: Een tekstveldwaar een geldig email adres ingevuld moet worden\r\ndoor de gebruiker.<br>\r\nTekstvak: Een simpel tekstvak.<br>\r\nAanvinkveld: Een enkel aanvinkveld<br>\r\nDrop-down veld: een drop-down veld. Gebruik het Mogelijke Waarden veld om\r\nelke weer te geven mogelijkheid in te voeren. Voer &eacute;&eacute;n optie\r\nin per regel.<br>\r\n<br>\r\n<b>Mogelijke Waarden</b><br>\r\nDit vled wordt alleen gebruikt voor het type drop-down veld. Voer de waarden\r\nin die in het drop-down veld weergegeven moeten worden, per regel &eacute;&eacute;n.<br>\r\n<br>\r\n<b>Standaard Waarde (optioneel)</b><br>\r\nVoer de eventuele standaard waarde van het veld in. Voor Ja/Nee velden voer\r\n&nbsp;\"ja \" of \"nee\" in om respectievelijk &nbsp;\"Ja\" &nbsp;en \"Nee\" van\r\nte voren te selecteren. Voor Aanvinkvelden voer \"aangevinkt\" in om het vak\r\nvante voren aan te vinken.<br>\r\n<br>\r\n<b>Verder gaan met toevoegen velden?</b><br>\r\nLaat dit aangevinkt staan als direct na het invoeren van het veld nog een\r\nveld ingevoerd wordt. <br>\r\n', 1037882523);
