insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'Item','Net als artikelen zijn objecten een soort Zwitsers zakmes van WebGUI. De\r\nmeeste stukken statische inhoud kunnen worden toegevoegd met behulp van Objecten,\r\nhoewel Objecten meestal een kleinere inhoud hebben dan Artikelen.<br>\r\n<br>\r\n<b>URL link</b><br>\r\nDit URL zal worden verbonden met dit Object.<br>\r\n<br>\r\n<i>Voorbeeld:</i> http://www.google.com<br>\r\n<br>\r\n<b>Bijlage</b><br>\r\nAls het gewenst is een tekstbestand, een zip-bestand of een ander willekeurig\r\ntype bestand toe te voegen als bijlage, die gedownload kan worden door gebruikers,\r\ndient dit bestand gekozen te worden van de harde schijf.<br>\r\n', 1037877488);
