insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'FileManager','<b>Bestandstitel</b>&nbsp;&nbsp; <br>\r\nDe titel die voor dit downloadbestand zal worden weergegeven.<br>\r\n<br>\r\n<b>Download Bestand<br>\r\n</b>Kies het bestand van de harde schijf dat in de wonload manager wordt\r\ningeladen.<br>\r\n<br>\r\n<b>Alternatieve Versie #1</b><br>\r\nEen alternatieve versie van het downloadbestand. Als het bestand bijvoorbeeld\r\neen JPEG is, kan de alternatieve versie een .TIF of .BMP formaat hebben.<br>\r\n<br>\r\n<b>Alternatieve Versie #2</b><br>\r\n Een alternatieve versie van het downloadbestand. Als het bestand bijvoorbeeld \r\neen JPEG is, kan de alternatieve versie een .TIF of .BMP formaat hebben.<br>\r\n<br>\r\n<b>Korte samenvatting</b><br>\r\nEen korte beschrijving van het bestand. Zorg dat er sleutelwoorden in staan\r\nwaar gebruikers naar zouden kunnen zoeken.<br>\r\n<br>\r\n<b>Download Groep</b><br>\r\nKies de groep die dit bestand kan downloaden.<br>\r\n<br>\r\n<b>Verder gaan naar toevoegen downloadbestanden?</b><br>\r\nKies \"Ja\" als er nog een bestand wordt toegevoegd in de downloadmanager.<br>\r\n', 1037876984);


