insert into international (internationalId,languageId,namespace,message,lastUpdated) values (828,3,'WebGUI','De meeste wobjects hebben sjablonen die de mogelijkheid geven om de layout\r\nvan de gebruikers interface van dat wobject aan te passen. Deze wobject sjablonen\r\nhebben een gezamenlijke set van variabelen die gebruikt kunnen worden voor\r\nde layout. Ze hebben ook een set eigen variabelen. De volgende sjabloon variabelen\r\nzijn beschikbaar om in alle wobjects de sjablonen aan te passen.<br>\r\n<p> <b>title</b><br>\r\nDe titel voor dit wobject. </p>\r\n<p>  <b>displayTitle</b><br>\r\nEen conditionele variabele die aangeeft of de titel wel of niet getoond moet\r\nworden. </p>\r\n<p>  <b>description</b><br>\r\nDe beschrijving van dit wobject. </p>\r\n<p>  <b>wobjectId</b><br>\r\nHet nummer dat WebGUI gebruikt om dit wobject te identificeren. </p>\r\n', 1039543288);
